// msg.payload.temp (Celsius) and msg.payload.humidity (%)
let T = msg.payload.temperature;
let RH = msg.payload.humidity;

// 1. Calculate SVP (Tetens equation)
// Result is in kPa (kilopascals)
let SVP = 0.61078 * Math.exp((17.27 * T) / (T + 237.3));

// 2. Calculate VPD
let VPD = SVP * (1 - (RH / 100));

// Output formatting
msg.payload = {
    vpd: parseFloat(VPD.toFixed(2)),
    status: VPD > 1.2 ? "Dry/High Stress" : (VPD < 0.4 ? "Mold Risk" : "Good")
};

return msg;

